def get_run_list(cosmics_or_collisions, dt_or_csc, require_pixels=True, require_rpc_good=False, min_run=0):
    if cosmics_or_collisions == 'cosmics':
        runs_dt    = cosmics_runs_dt
        runs_csc   = cosmics_runs_csc
        runs_strip = cosmics_runs_strip
        runs_pix   = cosmics_runs_pix
        runs_rpc   = cosmics_runs_rpc
    elif cosmics_or_collisions == 'collisions':
        runs_dt    = collisions_runs_dt
        runs_csc   = collisions_runs_csc
        runs_strip = collisions_runs_strip
        runs_pix   = collisions_runs_pix
        runs_rpc   = collisions_runs_rpc
    elif cosmics_or_collisions == 'both':
        runs_dt    = cosmics_runs_dt    | collisions_runs_dt
        runs_csc   = cosmics_runs_csc   | collisions_runs_csc
        runs_strip = cosmics_runs_strip | collisions_runs_strip
        runs_pix   = cosmics_runs_pix   | collisions_runs_pix
        runs_rpc   = cosmics_runs_rpc   | collisions_runs_rpc

    run_list = set(runs_strip) # copy
    
    if dt_or_csc == 'dt':
        run_list = run_list & runs_dt
    elif dt_or_csc == 'csc':
        run_list = run_list & runs_csc
    elif dt_or_csc == 'either':
        run_list = run_list & (runs_dt | runs_csc)
    if require_pixels:
        run_list = run_list & runs_pix
    if require_rpc_good:
        run_list = run_list & runs_rpc

    run_list = sorted(r for r in run_list if r >= min_run)

    # throw out runs 127476-127764 since they were reconstructed with the
    # wrong B field
    run_list = [r for r in run_list if r < 127476 or r > 127764]

    return run_list

